  FROM RepItemEx ri,
       SignalItem si,
       RTDBItem rt,
       PeriodType pt,
       (SELECT IID, REPLACE(SYS_CONNECT_BY_PATH(REPLACE(FolderName,'.',CHR(0)),'.'),CHR(0),'\.') Path
