  IF DELETING THEN
    IF (:OLD.ImgId IS NOT NULL) THEN
      DELETE
        FROM KMImage
       WHERE ImgId = :OLD.ImgId;
    END IF;
  END IF;

  IF UPDATING THEN
    IF (NVL(:OLD.ImgId,-1) <> NVL(:NEW.ImgId,-1)) AND (:OLD.ImgId IS NOT NULL) THEN
      DELETE
        FROM KMImage
       WHERE ImgId = :OLD.ImgId;
    END IF;
  END IF;
END;
