--  DDL for Trigger TAIDU_REPITEM_CFG
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TAIDU_REPITEM_CFG" 
  AFTER INSERT OR UPDATE OR DELETE ON RepItem
  FOR EACH ROW
DECLARE
  --TODO: define these strings as constants
  TRIGGER_NAME CONSTANT VARCHAR2(80) := 'cPIMSTagSync';
  MESSAGE      CONSTANT VARCHAR2(80) := 'LOG_ALERT';
  l_SigId1     NUMBER(9) := NULL;
  l_SigId2     NUMBER(9) := NULL;
BEGIN
  IF INSERTING THEN
    IF :NEW.RiClass <> 'CLC' THEN
      l_SigId1 := :NEW.VSigId1;
    END IF;
  ELSIF DELETING THEN
    IF :OLD.RiClass <> 'CLC' THEN
      l_SigId1 := :OLD.VSigId1;
    END IF;
  ELSIF UPDATING THEN
    IF :NEW.RiClass <> 'CLC' THEN
      IF :NEW.VSigId1 <> :OLD.VSigId1 THEN
        l_SigId1 := :NEW.VSigId1;
        l_SigId2 := :OLD.VSigId1;
      ELSIF NVL(:NEW.DeadBand, -1) <> NVL(:OLD.DeadBand, -1) THEN
        l_SigId1 := :NEW.VSigId1;
      END IF;
    END IF;
  END IF;

  -- process l_SigId2 if necessary
  IF l_SigId2 IS NOT NULL AND l_SigId2 <> -1 THEN
    INSERT
      INTO SignalLogCfgControl (SigId)
    VALUES (l_SigId2);
  END IF;

  -- process l_SigId1 and fire event if necessary
  IF l_SigId1 IS NOT NULL AND l_SigId1 <> -1 THEN
    INSERT
      INTO SignalLogCfgControl (SigId)
    VALUES (l_SigId1);

    SYS.DBMS_ALERT.SIGNAL(TRIGGER_NAME, MESSAGE);
  END IF;

  EXCEPTION
    WHEN DUP_VAL_ON_INDEX THEN
      NULL;
    WHEN OTHERS THEN
      NULL;
END;



