ALTER TRIGGER "BMI_CIMS"."TAIUD_REPITEM" ENABLE
--------------------------------------------------------
--  DDL for Trigger TAIU_CIMSCOMMENT
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TAIU_CIMSCOMMENT" 
  AFTER INSERT OR UPDATE ON CIMSComment
DECLARE
  CURSOR c1 IS
    SELECT cm.RemoteId, cm.PInstStart, cm.PType, cm.Validation, ri.RiClass, ri.LogClass
      FROM CimsComment cm, CimsCommentTmp ct, RepItem ri
     WHERE cm.CommentId = ct.CommentId
       AND cm.RemoteId  = ri.RiCode;

  CURSOR c2 (p_PType      PeriodInstance.PType%TYPE,
             p_PInstStart PeriodInstance.PInstStart%TYPE,
             p_RiCode     ChangedData.RiCode%TYPE) IS
    SELECT /*+ INDEX (cd UK_ChangedData) */
           cd.ROWID
      FROM ChangedData cd, PeriodInstance pi
     WHERE pi.PType      = c2.p_PType
       AND pi.PInstStart = c2.p_PInstStart
       AND pi.PInstId    = cd.PInstId
       AND cd.RiCode     = c2.p_RiCode;

  CURSOR c3 (p_PType      PeriodInstance.PType%TYPE,
             p_PInstStart PeriodInstance.PInstStart%TYPE,
             p_RiCode     RepData.RiCode%TYPE) IS
    SELECT /*+ INDEX (rd UK_RepData) */
           RD.PInstId
      FROM RepData rd, PeriodInstance pi
     WHERE pi.PType      = c3.p_PType
       AND pi.PInstStart = c3.p_PInstStart
       AND pi.PInstId    = rd.PInstId
       AND rd.RiCode     = c3.p_RiCode;

  CURSOR c4 (p_PType      PeriodInstance.PType%TYPE,
             p_PInstStart PeriodInstance.PInstStart%TYPE) IS
    SELECT PInstId
      FROM PeriodInstance
     WHERE PType      = c4.p_PType
       AND PInstStart = c4.p_PInstStart;

  c1_row  c1%ROWTYPE;
  c2_row  c2%ROWTYPE;
  c3_row  c3%ROWTYPE;
  c4_row  c4%ROWTYPE;
BEGIN
  -- Comment on a Log, export to regional server
  FOR c1_row IN c1 LOOP
    OPEN c2 (c1_row.PType, c1_row.PInstStart, c1_row.RemoteId);
    FETCH c2 INTO c2_row;
    IF c2%FOUND THEN
      UPDATE ChangedData
         SET ChangeNr   = ChangedDataSeq.NEXTVAL,
             Validation = c1_row.Validation
       WHERE ROWID = c2_row.ROWID;
    ELSE
      IF (c1_row.RiClass <> 'CLC') THEN
        OPEN c3 (c1_row.PType, c1_row.PInstStart, c1_row.RemoteId);
        FETCH c3 INTO c3_row;
        IF c3%FOUND THEN
          INSERT
            INTO ChangedData (PInstId, RiCode, ChangeNr, Validation)
           VALUES (c3_row.PInstId, c1_row.RemoteId, ChangedDataSeq.NEXTVAL, c1_row.Validation);
        END IF;
        CLOSE c3;
      ELSE
        OPEN c4 (c1_row.PType, c1_row.PInstStart);
        FETCH c4 INTO c4_row;
        IF c4%FOUND THEN
          INSERT
            INTO ChangedData (PInstId, RiCode, ChangeNr, Validation)
           VALUES (c4_row.PInstId, c1_row.RemoteId, ChangedDataSeq.NEXTVAL, c1_row.Validation);
        END IF;
        CLOSE c4;
      END IF;
    END IF;
    CLOSE c2;
 END LOOP;
END;



