      l_RiCode   := :NEW.RiCode;
      l_StopDate := :NEW.StopDate;
      l_OperType := 'D';
      makeChangesEntry := TRUE;
    END IF;
  END IF;

  -- Save StopLog changes
  IF makeChangesEntry THEN
    CIMSLIB.UpdateChangedStopLog (l_RiCode, l_StopDate, l_OperType);
