--  DDL for Trigger TBIU_CIMSCOMMENT
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TBIU_CIMSCOMMENT" 
  BEFORE INSERT OR UPDATE ON CIMSComment
  FOR EACH ROW
DECLARE
  -- Return RemoteURL
  CURSOR c1 (p_LocName    SampleList.LocName%TYPE,
             p_MatName    SampleList.MatName%TYPE,
             p_SampleDate SampleList.SampleDate%TYPE) IS
    SELECT sa.Name AttrName, sa.TextValue RemoteUrl, sl.SampleListId
      FROM SampleAttr sa, SampleList sl
     WHERE sl.LocName      = c1.p_LocName
       AND sl.MatName      = c1.p_MatName
       AND sl.SampleDate   = c1.p_SampleDate
       AND sl.SampleListId = sa.RemoteId
       AND sa.AttrType     = 0
       AND sa.DataType     = 8 /* Remote Node */
       AND sa.Name         = 'returnaddress'
       AND sa.TextValue IS NOT NULL;

  -- Return job ID
  CURSOR c2 (p_SampleListId SampleWorkList.SampleListId%TYPE,
             p_RiCode       RepItem.RiCode%TYPE) IS
    SELECT sw.DefId
      FROM SampleWorkList sw, MeasureHistory mh, RepItem ri
     WHERE sw.SampleListId = c2.p_SampleListId
       AND sw.JobId        = mh.JobId
       AND mh.SigId        = ri.VSigId1
       AND ri.RiClass      = 'AVG'
       AND ri.LogClass     = 'L'
       AND ri.RiCode       = c2.p_RiCode;

  l_DefId      RemoteJobFIFO.DefId%TYPE;
  l_EntryType  RemoteJobFIFO.EntryType%TYPE;
  makeEntry    BOOLEAN := FALSE;
BEGIN
  IF INSERTING OR :OLD.ParentId = :NEW.ParentId THEN
    :NEW.DefDate := SYSDATE;
  END IF;

  IF (:NEW.PType = 'PRI') THEN
    FOR c1_row IN c1 (:NEW.SampleLoc, :NEW.SortName, :NEW.PInstStart) LOOP
      /* Send COMMENTS 'C' (Central LAB --> Plant LAB) */
      IF (c1_row.AttrName = 'returnaddress') THEN
        -- Comment on a job
        IF (:NEW.TypeId = 23) THEN
          makeEntry := TRUE;
          l_DefId := :NEW.RemoteId;
          l_EntryType := 'C';
        END IF;

        -- Comment on a log
        IF (:NEW.TypeId = 4) THEN
          OPEN c2 (c1_row.SampleListId, :NEW.RemoteId);
          FETCH c2 INTO l_DefId;
          IF c2%FOUND THEN
            makeEntry := TRUE;
            l_EntryType := 'C';
          END IF;
          CLOSE c2;
        END IF;
      END IF;

      -- This can never be executed, because c1 can only return 'returnaddress' attributes
      --/* Send COMMENTS 'B' (Central LAB --> Plant LAB) */
      --IF (c1_row.AttrName LIKE 'transferaddress%') THEN
      --  -- Comment on a job
      --  IF (:NEW.TypeId = 23) THEN
      --    makeEntry := TRUE;
      --    l_DefId := :NEW.RemoteId;
      --    l_EntryType := 'B';
      --  END IF;

      --  -- Comment on a log
      --  IF (:NEW.TypeId = 4) THEN
      --    OPEN c2 (c1_row.SampleListId, :NEW.RemoteId);
      --    FETCH c2 INTO l_DefId;
      --    IF c2%FOUND THEN
      --      makeEntry := TRUE;
      --      l_EntryType := 'B';
      --    END IF;
      --    CLOSE c2;
      --  END IF;
      --END IF;

      -- Make an entry in the Fifo
      IF makeEntry THEN
        INSERT
          INTO RemoteJobFIFO (JobSeq, EntryType, SampleListId, DefId, EntryTime,
                              RemoteURL, CommentId)
        VALUES (RemoteJobFIFOSeq.NEXTVAL, l_EntryType, c1_row.SampleListId, l_DefId, SYSDATE,
                c1_row.RemoteUrl, :NEW.CommentId);
      END IF;
    END LOOP;
  END IF;
END;
