--  DDL for Trigger TBIU_REPDATA
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TBIU_REPDATA" 
  BEFORE INSERT OR UPDATE OR DELETE ON RepData
  FOR EACH ROW
BEGIN
  -- Insert record
  IF INSERTING THEN
    -- This record should not be exported, return
    IF (:NEW.Dummy = 1) OR (:NEW.PctQual = -1) THEN
      RETURN;
    END IF;

    -- This record should be exported, register it in the ChangedData table
    CIMSLIB.UpdateChangedData (:NEW.PInstId, :NEW.RiCode);
  END IF;

  -- Update record
  IF UPDATING THEN
    -- This record should not be exported, return
    IF (:NEW.Dummy = 1) OR (:OLD.Dummy = 1) OR (:NEW.PctQual = -1) THEN
      IF (:NEW.Dummy = 1) THEN
        :NEW.Dummy := :OLD.Dummy;
      END IF;

      RETURN;
    END IF;

    -- This record should be exported, register it in the ChangedData table
    CIMSLIB.UpdateChangedData (:NEW.PInstId, :NEW.RiCode);
  END IF;

  -- Delete record
  IF DELETING THEN
    IF (:OLD.Dummy = 1) OR (:OLD.PctQual = -1) THEN
      RETURN;
    END IF;

    -- This record should be exported, register it in the ChangedData table
    CIMSLIB.UpdateChangedData (:OLD.PInstId, :OLD.RiCode);
  END IF;
END;



