--  DDL for Trigger TBIU_ANALYSISMEASURE
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TBIU_ANALYSISMEASURE" 
  BEFORE INSERT OR UPDATE ON AnalysisMeasure
  FOR EACH ROW
DECLARE
  saveLimit   BOOLEAN := FALSE;
  l_UserName  LimitsHistory.UserName%TYPE;
BEGIN
  :NEW.DefDate := SYSDATE;
  IF :NEW.AliasName IS NULL THEN
    :NEW.AliasName  := :NEW.MeasureName;
  END IF;

  -- Ignore attributes
  saveLimit := :NEW.SigId IS NOT NULL;

  -- Ignore empty records
  IF INSERTING THEN
    saveLimit := ((:NEW.TargetValue IS NOT NULL) OR
                  (:NEW.HiLimit IS NOT NULL) OR
                  (:NEW.HiHiLimit IS NOT NULL) OR
                  (:NEW.HiHiHiLimit IS NOT NULL) OR
                  (:NEW.LoLimit IS NOT NULL) OR
                  (:NEW.LoLoLimit IS NOT NULL) OR
                  (:NEW.LoLoLoLimit IS NOT NULL));
  END IF;

  -- Check changes
  IF UPDATING THEN
    saveLimit := ((:OLD.LoLimit <> :NEW.LoLimit) OR
                  (:OLD.LoLimit IS NULL AND :NEW.LoLimit IS NOT NULL) OR
                  (:OLD.LoLimit IS NOT NULL AND :NEW.LoLimit IS NULL) OR

                  (:OLD.HiLimit <> :NEW.HiLimit) OR
                  (:OLD.HiLimit IS NULL AND :NEW.HiLimit IS NOT NULL) OR
                  (:OLD.HiLimit IS NOT NULL AND :NEW.HiLimit IS NULL) OR

                  (:OLD.LoLoLimit <> :NEW.LoLoLimit) OR
                  (:OLD.LoLoLimit IS NULL AND :NEW.LoLoLimit IS NOT NULL) OR
                  (:OLD.LoLoLimit IS NOT NULL AND :NEW.LoLoLimit IS NULL) OR

                  (:OLD.HiHiLimit <> :NEW.HiHiLimit) OR
                  (:OLD.HiHiLimit IS NULL AND :NEW.HiHiLimit IS NOT NULL) OR
                  (:OLD.HiHiLimit IS NOT NULL AND :NEW.HiHiLimit IS NULL) OR

                  (:OLD.LoLoLoLimit <> :NEW.LoLoLoLimit) OR
                  (:OLD.LoLoLoLimit IS NULL AND :NEW.LoLoLoLimit IS NOT NULL) OR
                  (:OLD.LoLoLoLimit IS NOT NULL AND :NEW.LoLoLoLimit IS NULL) OR

                  (:OLD.HiHiHiLimit <> :NEW.HiHiHiLimit) OR
                  (:OLD.HiHiHiLimit IS NULL AND :NEW.HiHiHiLimit IS NOT NULL) OR
                  (:OLD.HiHiHiLimit IS NOT NULL AND :NEW.HiHiHiLimit IS NULL) OR

                  (:OLD.TargetValue <> :NEW.TargetValue) OR
                  (:OLD.TargetValue IS NULL AND :NEW.TargetValue IS NOT NULL) OR
                  (:OLD.TargetValue IS NOT NULL AND :NEW.TargetValue IS NULL));
  END IF;

  IF saveLimit THEN
    -- Get user name
    SELECT NVL(SYS_CONTEXT('USERENV','CLIENT_IDENTIFIER'), SYS_CONTEXT('USERENV','OS_USER'))
      INTO l_UserName
      FROM DUAL;

    SmplLib.SaveAnaMeasureLimit (:NEW.DefId,
                                 SYSDATE,
                                 :NEW.MeasureId,
                                 :NEW.SigId,
                                 :NEW.LoLimit,
                                 :NEW.HiLimit,
                                 :NEW.LoLoLimit,
                                 :NEW.HiHiLimit,
                                 :NEW.LoLoLoLimit,
                                 :NEW.HiHiHiLimit,
                                 :NEW.TargetValue,
                                 l_UserName);
  END IF;

  EXCEPTION
    WHEN OTHERS THEN
      CIMSLIB.LogError ('TBIU_AnalysisMeasure: Unhandled Error', 'E', SQLCODE);
END;



