--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TAIDU_ANAMEASURELIMITS" 
  BEFORE INSERT OR DELETE OR UPDATE ON AnaMeasureLimits
  FOR EACH ROW

DECLARE
  CURSOR c1 (P_MeasureId AnalysisMeasure.MeasureId%TYPE) IS
    SELECT TargetValue,
           HiLimit, HiHiLimit, HiHiHiLimit,
           LoLimit, LoLoLimit, LoLoLoLimit
      FROM AnalysisMeasure
     WHERE MeasureId = c1.P_MeasureId;

  c1_row  c1%ROWTYPE;
  mutating_trigger EXCEPTION;
  PRAGMA EXCEPTION_INIT(mutating_trigger, -04091);
  l_UserName  LimitsHistory.UserName%TYPE;
BEGIN /* TAIDU_AnaMeasureLimits */
  /* Log a message */
  SELECT NVL(SYS_CONTEXT('USERENV','CLIENT_IDENTIFIER'), SYS_CONTEXT('USERENV','OS_USER'))
    INTO l_UserName
    FROM DUAL;

  -- Update the latest record with the configuration limits
  IF INSERTING OR UPDATING THEN
    SmplLib.StoreSampleLimit (:NEW.MatId,
                              SYSDATE,
                              :NEW.MeasureId,
                              :NEW.LoLimit,
                              :NEW.HiLimit,
                              :NEW.LoLoLimit,
                              :NEW.HiHiLimit,
                              :NEW.LoLoLoLimit,
                              :NEW.HiHiHiLimit,
                              :NEW.TargetValue,
                              l_UserName);
  END IF;

  -- Take the limits from AnalysisMeasure table
  IF DELETING THEN
    OPEN c1 (:OLD.MeasureId);
    FETCH c1 INTO c1_row;
    IF c1%FOUND THEN
      SmplLib.StoreSampleLimit (:OLD.MatId,
                                SYSDATE,
                                :OLD.MeasureId,
                                c1_row.LoLimit,
                                c1_row.HiLimit,
                                c1_row.LoLoLimit,
                                c1_row.HiHiLimit,
                                c1_row.LoLoLoLimit,
                                c1_row.HiHiHiLimit,
                                c1_row.TargetValue,
                                l_UserName);
    END IF;
  END IF;

  EXCEPTION
    WHEN mutating_trigger THEN
      NULL; -- The record is deleted from AnalysisMeasure, ==> nothing to do
    WHEN OTHERS THEN
      CIMSLIB.LogError ('TAIDU_AnaMeasureLimits: Unhandled Error', 'E', SQLCODE);
END;



