--  DDL for Trigger TAIDU_CIMSPROPERTYCONTENT
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TAIDU_CIMSPROPERTYCONTENT" 
  FOR INSERT OR UPDATE OR DELETE ON CIMSProperty
  COMPOUND TRIGGER
    TYPE OperChangedTbl   IS TABLE OF NUMBER(1) INDEX BY SIMPLE_INTEGER;
    TYPE PropIdChangedTbl IS TABLE OF CIMSproperty.PropId%TYPE INDEX BY SIMPLE_INTEGER;

    l_OperChng    OperChangedTbl;
    l_PropIdChng  PropIdChangedTbl;
    idx           SIMPLE_INTEGER := 0;

  AFTER EACH ROW IS
  BEGIN
    IF INSERTING THEN
      l_OperChng(idx) := C30Lib.CFG_INSERT;
      l_PropIdChng(idx) := :NEW.PropId;
    ELSIF DELETING THEN
      l_OperChng(idx) := C30Lib.CFG_DELETE;
      l_PropIdChng(idx) := :OLD.PropId;
    ELSIF UPDATING THEN
      l_OperChng(idx) := C30Lib.CFG_UPDATE;
      l_PropIdChng(idx) := :OLD.PropId;
    END IF;
    idx := idx + 1;
  END AFTER EACH ROW;

  AFTER STATEMENT IS
    l_RiCodeOld   RepItem.RiCode%TYPE;
    l_RiCodeNew   RepItem.RiCode%TYPE;
    l_FormulaOld  VARCHAR2(4000);
    l_FormulaNew  VARCHAR2(4000);
    l_Oper        NUMBER;
    l_ChId        NUMBER;

  BEGIN
    IF l_PropIdChng.Count > 0 THEN
      FOR i IN l_PropIdChng.FIRST .. l_PropIdChng.LAST LOOP
        -- reset local variables
        l_RiCodeOld := NULL;
        l_RiCodeNew := NULL;
        l_FormulaOld := NULL;
        l_FormulaNew := NULL;

        -- read old values
        IF l_OperChng(i) != C30Lib.CFG_INSERT THEN
          C30Lib.ReadOldCLCValues(l_PropIdChng(i), l_RiCodeOld, l_FormulaOld);
        END IF;

        -- read new values
        IF l_OperChng(i) != C30Lib.CFG_DELETE THEN
          C30Lib.ReadNewCLCValues(l_PropIdChng(i), l_RiCodeNew, l_FormulaNew);
        END IF;

        l_RicodeNew := NVL(l_RiCodeNew, l_RiCodeOld);
        IF l_RicodeNew IS NOT NULL THEN
          l_Oper := l_OperChng(i);
          l_ChId := NULL;
          C30Lib.AuditCfgField(4, l_RicodeNew, NULL, 'FORMULA', 'CONTENTS', l_Oper, l_ChId, l_FormulaOld, l_FormulaNew);
        END IF;
      END LOOP;
    END IF;
  END AFTER STATEMENT;
END TAIDU_CimsPropertyContent;



