ALTER TRIGGER "BMI_CIMS"."TBIU_PERIODTYPE" ENABLE
--------------------------------------------------------
--  DDL for Trigger TBIU_PERIODTYPEEXT
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TBIU_PERIODTYPEEXT" 
  BEFORE INSERT OR UPDATE ON PeriodTypeExt
  FOR EACH ROW
DECLARE
  CURSOR c1 (p_PType PeriodType.PType%TYPE) IS
    SELECT NVL(ConsInterval,60) ConsIntervalMin
      FROM PeriodType
     WHERE PType = c1.p_PType;

  l_OldConsTime    PeriodTypeExt.ConsTime%TYPE;
  l_NewConsTime    PeriodTypeExt.ConsTime%TYPE;
  ConsIntervalMin  NUMBER;
BEGIN
  IF (:NEW.ConsTime > :NEW.NextUpdate) OR
     (:NEW.ConsTime <= :NEW.LastUpdate) THEN

    -- Save ConsTime for debug message
    l_OldConsTime := :NEW.ConsTime;

    -- Set ConsTime to current hour
    l_NewConsTime := TRUNC(SYSDATE,'HH24');

    -- Period is in the past
    IF (l_NewConsTime >= :NEW.NextUpdate) THEN
      l_NewConsTime := :NEW.NextUpdate;
    END IF;

    -- Period is in the future
    IF (l_NewConsTime <= :NEW.LastUpdate) THEN
      OPEN c1 (:NEW.PType);
      FETCH c1 INTO ConsIntervalMin;
      CLOSE c1;
      l_NewConsTime := :NEW.LastUpdate + NUMTODSINTERVAL(ConsIntervalMin, 'MINUTE');
    END IF;

    -- Set the new ConsTime
    :NEW.ConsTime := l_NewConsTime;

    CIMSLIB.LogError ('TBIU_PeriodTypeExt: Correcting ' || :NEW.PType ||
                      ' GroupNr=' || TO_CHAR(:NEW.GroupNr) ||
                      ', LastUpdate=' || TO_CHAR(:NEW.LastUpdate, 'DD-MM-YYYY HH24:MI') ||
                      ', NextUpdate=' || TO_CHAR(:NEW.NextUpdate, 'DD-MM-YYYY HH24:MI') ||
                      ', OldConsTime=' || TO_CHAR(l_OldConsTime, 'DD-MM-YYYY HH24:MI') ||
                      ', NewConsTime=' || TO_CHAR(l_NewConsTime, 'DD-MM-YYYY HH24:MI'),
                      'E');
  END IF;
END;
