ALTER TRIGGER "BMI_CIMS"."TBD_REPORTFILTER" ENABLE
--------------------------------------------------------
--  DDL for Trigger TAIU_REPREPITEMEXT
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TAIU_REPREPITEMEXT" 
  BEFORE INSERT OR UPDATE ON RepRepItemExt
  FOR EACH ROW
DECLARE
  replicatedAlready  BOOLEAN := FALSE;
BEGIN
  IF INSERTING OR
     (UPDATING AND (:OLD.NodeNr <> :NEW.NodeNr OR :OLD.RemoteRiText <> :NEW.RemoteRiText
                 OR :OLD.AggrFunc <> :NEW.AggrFunc OR :OLD.RepPlan <> :NEW.RepPlan)) THEN
    /* trigger a replication whenever config changes  */
    IF :NEW.RemoteRiCode = -999 THEN
      /* If we update RemoteRiText from service's synching logic then we don't want to
         re-replicate. To prevent this we update RemoteRiCode to -999 at the same time */
      :NEW.RemoteRiCode := :OLD.RemoteRiCode;
    ELSE
      REPLIB.ReplicateLogAll (:New.RiCode);
      /* replicate log all would have already triggered the stop log request so no need to enter again  */
      replicatedAlready := TRUE;
    END IF;
  END IF;
  IF replicatedAlready = FALSE AND :NEW.StopLog = -1 THEN
    IF INSERTING OR
       (UPDATING AND (:OLD.StopLog IS NULL OR :OLD.StopLog = 0)) THEN
      REPLIB.InsertStopLogRepRequest (:New.RiCode);
    END IF;
  END IF;
END;
