--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TAIDU_SAMPLEWORKLIST" 
  BEFORE INSERT OR DELETE OR UPDATE ON SampleWorkList
  FOR EACH ROW
DECLARE
  l_ModuleName  VARCHAR2(64);
BEGIN
  -- Update timestamp
  IF INSERTING OR UPDATING THEN
    :NEW.LastUpdate := SYSDATE;
  END IF;

  -- Get DB module, return if triggered by LIMSImport tool
  SELECT NVL(SYS_CONTEXT('USERENV','MODULE'),'Unknown')
    INTO l_ModuleName
    FROM DUAL;

  IF DELETING AND (l_ModuleName <> 'LIMSImport') THEN
    SmplLib.JobDeleteEvent (:OLD.SampleListId, :OLD.DefId, :OLD.AnalysisStatus,
                            :OLD.MeasDate);
  END IF;

  IF INSERTING AND (l_ModuleName <> 'LIMSImport') THEN
    SmplLib.JobInsertEvent (:NEW.SampleListId, :NEW.DefId, :NEW.AnalysisStatus,
                            :NEW.MeasDate);
  END IF;

  IF UPDATING AND (l_ModuleName <> 'LIMSImport') THEN
    SmplLib.JobUpdateEvent (:NEW.SampleListId, :NEW.DefId, :NEW.JobId, :NEW.AnalysisStatus,
                            :NEW.ApprovedBy, :NEW.ApprovalDate, :NEW.MeasDate);
  END IF;

  -- Delete job attributes
  IF DELETING THEN
    DELETE JobAttrView
     WHERE JobId = :OLD.JobId;

    -- Delete comments on job
    DELETE CIMSComment
     WHERE TypeId   = 23
       AND RemoteId = :OLD.DefId
       AND (PInstStart, SampleLoc, SortName) = (SELECT SampleDate, LocName, MatName
                                                  FROM SampleList
                                                 WHERE SampleListId = :OLD.SampleListId);
  END IF;
END;
