--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TBD_SIGNALITEM" 
  BEFORE DELETE ON SignalItem
  FOR EACH ROW
DECLARE
  /* Declare local variable for the sigid which has to be deleted */
   DelSigId SignalItem.SigId%TYPE;
BEGIN
/* Before the signal in the SignalItem table is deleted this trigger   */
/* is fired. In case a repitem is related to the specific signal which */
/* has to be deleted, this trigger writes -1 into the related item in  */
/* the RepItem Table                                                   */
  DelSigId := :OLD.SigId;
  UPDATE RepItem A SET (VSIGID1,VSIGID2,VSIGID3,VSIGID4,VSIGID5,
                        VSIGID6,VSIGID7,VSIGID8,VSIGID9,VSIGID10,
                        CSIGID1,CSIGID2,CSIGID3) =
     (SELECT DECODE(VSIGID1,  DelSigId, -1,   NULL, NULL, VSIGID1),
             DECODE(VSIGID2,  DelSigId, -1,   NULL, NULL, VSIGID2),
             DECODE(VSIGID3,  DelSigId, -1,   NULL, NULL, VSIGID3),
             DECODE(VSIGID4,  DelSigId, -1,   NULL, NULL, VSIGID4),
             DECODE(VSIGID5,  DelSigId, -1,   NULL, NULL, VSIGID5),
             DECODE(VSIGID6,  DelSigId, -1,   NULL, NULL, VSIGID6),
             DECODE(VSIGID7,  DelSigId, -1,   NULL, NULL, VSIGID7),
             DECODE(VSIGID8,  DelSigId, -1,   NULL, NULL, VSIGID8),
             DECODE(VSIGID9,  DelSigId, -1,   NULL, NULL, VSIGID9),
             DECODE(VSIGID10, DelSigId, -1,   NULL, NULL, VSIGID10),
             DECODE(CSIGID1,  DelSigId, -1,   NULL, NULL, CSIGID1),
             DECODE(CSIGID2,  DelSigId, -1,   NULL, NULL, CSIGID2),
             DECODE(CSIGID3,  DelSigId, -1,   NULL, NULL, CSIGID3)
        FROM RepItem B
       WHERE A.RiCode = B.RiCode)
   WHERE RiCode IN (
      SELECT RiCode
        FROM RepItem
       WHERE RiClass != 'CLC'
         AND DelSigId IN (VSIGID1, VSIGID2, VSIGID3, VSIGID4, VSIGID5,
                          VSIGID6, VSIGID7, VSIGID8, VSIGID9, VSIGID10,
                          CSIGID1, CSIGID2, CSIGID3));
  UPDATE RepItem A SET (OSIGID1) =
     (SELECT DECODE(OSIGID1,  DelSigId, -1,   NULL, NULL, OSIGID1)
        FROM RepItem B
       WHERE A.RiCode = B.RiCode)
   WHERE RiCode IN (
      SELECT RiCode
        FROM RepItem
       WHERE DelSigId IN (OSIGID1));
END;



