  CREATE OR REPLACE EDITIONABLE TRIGGER "BMI_CIMS"."TBIDU_STOPLOG" 
  BEFORE INSERT OR DELETE OR UPDATE ON StopLog
  FOR EACH ROW
DECLARE
  l_RiCode       StopLog.RiCode%TYPE;
  l_StopDate     StopLog.StopDate%TYPE;
  l_RestartDate  StopLog.RestartDate%TYPE;
BEGIN
  -- Setup parameters
  IF INSERTING THEN
    -- Return if called from DataAc
    IF (:NEW.UserName = 'DATAAC') THEN
      :NEW.UserName := NULL;
      RETURN;
    END IF;

    l_RiCode      := :NEW.RiCode;
    l_StopDate    := :NEW.StopDate;
    l_RestartDate := :NEW.RestartDate;
  END IF;

  IF DELETING THEN
    l_RiCode      := :OLD.RiCode;
    l_StopDate    := :OLD.StopDate;
    l_RestartDate := :OLD.RestartDate;
  END IF;

  IF UPDATING THEN
    -- Return if called from DataAc
    IF (:NEW.UserName = 'DATAAC') THEN
      :NEW.UserName := :OLD.UserName;
      RETURN;
    END IF;

    l_RiCode      := :NEW.RiCode;
    l_StopDate    := LEAST(:OLD.StopDate,:NEW.StopDate);
    l_RestartDate := GREATEST(:OLD.RestartDate,:NEW.RestartDate);
  END IF;

  -- Register changes
  CIMSLIB.StopLogChanged (l_RiCode, l_StopDate, l_RestartDate);
END;



