set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "BMI_CIMS"."CHANGEDATATYPE" (tableName in varchar2, columnName in varchar2, changeStatement in varchar2)
AS
  cnt PLS_INTEGER;
BEGIN
  DECLARE
    var_pos number;
    var_pos_l number;
    var_pos_r number;
    var_pos_m number;
    var_data_length varchar2(255);
    var_data_precision number;
    var_data_scale number;
    var_do_execute boolean;
  BEGIN

    -- Process VARCHAR2
    var_pos :=  INSTR(UPPER(changeStatement),UPPER('varchar2'));
    if (var_pos > 0) then
        var_pos_l := INSTR(changeStatement,'(');
        var_pos_r := INSTR(changeStatement,')');
        var_data_length := TRIM(SUBSTR(changeStatement,var_pos_l+1,var_pos_r-var_pos_l-1));
        SELECT count(1) INTO cnt  FROM USER_TAB_COLUMNS WHERE UPPER(table_name) = UPPER(tableName) AND UPPER(column_name) = UPPER(columnName) AND UPPER(data_type) = UPPER('varchar2') AND UPPER(data_length) = var_data_length;
        var_do_execute := ( cnt < 1 );
    end if;

    -- Process NUMBER(p, s)
    var_pos :=  INSTR(UPPER(changeStatement),UPPER('number'));
    if (var_pos > 0) then
      var_pos_l := INSTR(changeStatement,'(');
      var_pos_r := INSTR(changeStatement,')');
      var_pos_m := INSTR(changeStatement,',',var_pos_l);
      var_data_precision := TRIM(SUBSTR(changeStatement,var_pos_l+1,var_pos_r-var_pos_m));
      var_data_scale := TRIM(SUBSTR(changeStatement,var_pos_m+1,var_pos_r-var_pos_m));
      SELECT count(1) INTO cnt  FROM USER_TAB_COLUMNS WHERE UPPER(table_name) = UPPER(tableName) AND UPPER(column_name) = UPPER(columnName) AND UPPER(data_type) = UPPER('number') AND UPPER(data_precision) = var_data_precision AND UPPER(data_scale) = var_data_scale;
      var_do_execute := ( cnt < 1 );
    end if;

    if( var_do_execute ) then
      Execute Immediate changeStatement;
      DBMS_OUTPUT.PUT_LINE('OK: ' || changeStatement);
    end if;

  EXCEPTION WHEN OTHERS THEN
    DBMS_OUTPUT.PUT_LINE('Error ORA-' || TO_CHAR(SQLCODE) || ' ' || changeStatement);
  END;
END;
