  CREATE OR REPLACE EDITIONABLE PACKAGE BODY "BMI_CIMS"."GENSAMPLEID" AS     FUNCTION GetSampleSeqNo (p_SeqName    IN VARCHAR2, 
                           p_SampleDate IN DATE) RETURN NUMBER IS 
 
    CURSOR c1 (p_SeqRef IN NUMBER) IS 
      SELECT SeqValue 
        FROM SampleIDSeq 
       WHERE SeqName     = p_SeqName 
         AND SeqStartRef = p_SeqRef 
         FOR UPDATE; 
 
    SeqRef NUMBER; 
    SeqVal NUMBER; 
  BEGIN 
    SeqRef := TO_NUMBER (TO_CHAR (p_SampleDate,'YYYY')); 
 
    OPEN C1 (SeqRef); 
    FETCH c1 INTO SeqVal; 
    IF c1%FOUND THEN 
      SeqVal := SeqVal + 1; 
      UPDATE SampleIDSeq 
         SET SeqValue = SeqVal 
       WHERE CURRENT OF c1; 
    ELSE 
      SeqVal := 1; 
      INSERT INTO SampleIDSeq (SeqName, SeqStartRef, SeqValue) 
           VALUES (p_SeqName, SeqRef, SeqVal); 
    END IF; 
    CLOSE c1; 
    COMMIT WORK; 
 
    RETURN SeqVal; 
  END GetSampleSeqNo; 
 
/*----------------------------------------------------------------------*/ 
 
  PROCEDURE GetSampleID (SampleDate IN  SampleList.SampleDate%TYPE, 
                         MatName    IN  SampleList.MatName%TYPE, 
                         LocName    IN  SampleList.LocName%TYPE, 
                         SampleID   OUT SampleList.SampleID%TYPE, 
                         PlantName  IN  VARCHAR2 := NULL) IS 
 
    Plant    VARCHAR2(20); 
    MatAlias VARCHAR2(20); 
    Loc      VARCHAR2(20); 
    SeqNb    NUMBER; 
  
    CURSOR c1 (p_Locname SampleList.LocName%TYPE) IS 
      Select Substr(Upper(AliasName) || 'XXX', 0, 3) 
        From MaterialGroup 
       Where GroupID = (Select RemoteID 
                          From CIMSItem 
                         Where TypeID = 25 
                           And ParentID = 25 
                       Connect By IId = PRIOR ParentID 
                         Start With RemoteID = (Select MatGroup 
                                                  From SampleLocation sl 
                                                 Where sl.LocName = p_Locname) 
         And TypeID = 25); 
 
   BEGIN /* GetSampleID */ 
    Plant := SUBSTR(NVL(PlantName, ''), 1, 3); -- first 3 characters 
 
    OPEN c1 (LocName); 
    FETCH c1 INTO MatAlias; 
    IF (C1%NOTFOUND) THEN 
      CIMSLIB.LogError ('GetSampleID: location or material not found. Location=' || LocName || ' Material=' || MatName, 'W'); 
      CLOSE c1; 
      RETURN; 
    END IF; 
    CLOSE c1; 
    SeqNb := GetSampleSeqNo (NVL(Plant || MatAlias, ' '), SampleDate); 
    SampleID := MatAlias || TO_CHAR(SampleDate, 'YY') || TO_CHAR(SeqNb, 'FM00000'); 
  END GetSampleID; 
 
/*----------------------------------------------------------------------*/ 
 
  PROCEDURE GetSampleInfo (P_SampleID    IN  SampleList.SampleID%TYPE, 
                           P_EquipID     IN  Equipment.EquipID%TYPE, 
                           P_SampleDate  OUT VARCHAR2, /* DD-MM-YYYY HH24:MI:SS */ 
                           P_MatName     OUT SampleList.MatName%TYPE, 
                           P_LocName     OUT SampleList.LocName%TYPE, 
                           P_SampleFound OUT NUMBER) IS 
    CURSOR c1 (P_SampleID SampleList.SampleID%TYPE) IS 
      SELECT LocName, 
             TO_CHAR(SampleDate, 'DD-MM-YYYY HH24:MI:SS'), 
             MatName 
        FROM SampleList 
       WHERE SampleId   = c1.P_SampleID 
         AND SampleDate > SYSDATE - 180; 
 
    CURSOR c2 (P_EquipID Equipment.EquipID%TYPE) IS 
      SELECT EquipName 
        FROM Equipment 
       WHERE EquipID = c2.P_EquipID; 
 
    c1_row       c1%ROWTYPE; 
    L_EquipName  Equipment.EquipName%TYPE; 
  BEGIN /* GetSampleInfo */ 
    /* Get sample information */ 
    OPEN c1 (P_SampleID); 
    FETCH c1 INTO P_LocName, P_SampleDate, P_MatName; 
    IF c1%FOUND THEN 
      /* Check if there exists more than one sample */ 
      FETCH c1 INTO c1_row; 
      IF c1%FOUND THEN 
        /* Log an error message */ 
        OPEN c2 (P_EquipID); 
        FETCH c2 INTO L_EquipName; 
        CLOSE c2; 
        CIMSLIB.LogError ('GetSampleInfo: multiple samples error. SampleID=' || P_SampleID || ' Equipment=' || L_EquipName, 'E'); 
        P_SampleFound := 0; 
      ELSE 
        P_SampleFound := 1; 
      END IF; 
 
    ELSE 
      P_SampleFound := 0; 
    END IF; 
 
    CLOSE c1; 
  END GetSampleInfo; 
 
 END GENSAMPLEID;
