  END GetSampleID;

/*----------------------------------------------------------------------*/

  PROCEDURE GetSampleInfo (P_SampleID    IN  SampleList.SampleID%TYPE,
                           P_EquipID     IN  Equipment.EquipID%TYPE,
                           P_SampleDate  OUT VARCHAR2, /* DD-MM-YYYY HH24:MI:SS */
                           P_MatName     OUT SampleList.MatName%TYPE,
                           P_LocName     OUT SampleList.LocName%TYPE,
                           P_SampleFound OUT NUMBER) IS
    CURSOR c1 (P_SampleID SampleList.SampleID%TYPE) IS
      SELECT LocName,
             TO_CHAR(SampleDate, 'DD-MM-YYYY HH24:MI:SS'),
             MatName
        FROM SampleList
       WHERE SampleId   = c1.P_SampleID
         AND SampleDate > SYSDATE - 180;

    CURSOR c2 (P_EquipID Equipment.EquipID%TYPE) IS
      SELECT EquipName
        FROM Equipment
       WHERE EquipID = c2.P_EquipID;

    c1_row       c1%ROWTYPE;
    L_EquipName  Equipment.EquipName%TYPE;
  BEGIN /* GetSampleInfo */
    /* Get sample information */
    OPEN c1 (P_SampleID);
    FETCH c1 INTO P_LocName, P_SampleDate, P_MatName;
    IF c1%FOUND THEN
      /* Check if there exists more than one sample */
      FETCH c1 INTO c1_row;
      IF c1%FOUND THEN
        /* Log an error message */
        OPEN c2 (P_EquipID);
        FETCH c2 INTO L_EquipName;
        CLOSE c2;
        CIMSLIB.LogError ('GetSampleInfo: multiple samples error. SampleID=' || P_SampleID || ' Equipment=' || L_EquipName, 'E');
        P_SampleFound := 0;
      ELSE
        P_SampleFound := 1;
      END IF;

    ELSE
      P_SampleFound := 0;
    END IF;

    CLOSE c1;
  END GetSampleInfo;

END GENSAMPLEID_TMP;
