      SELECT SeqValue
        FROM LIMSIdSeq
       WHERE SeqName     = p_SeqName
         AND SeqStartRef = p_SeqRef
         FOR UPDATE;

    SeqRef  NUMBER;
    SeqVal  NUMBER;
  BEGIN
    SeqRef := TO_NUMBER (TO_CHAR (p_SampleDate,'YYYY'));

    OPEN C1 (SeqRef);
    FETCH c1 INTO SeqVal;
    IF c1%FOUND THEN
      SeqVal := SeqVal + 1;
      UPDATE LIMSIdSeq
         SET SeqValue = SeqVal
       WHERE CURRENT OF c1;
    ELSE
      SeqVal := 1;
      INSERT
        INTO LIMSIdSeq (SeqName, SeqStartRef, SeqValue)
      VALUES (p_SeqName, SeqRef, SeqVal);
    END IF;
    CLOSE c1;
    COMMIT WORK;

    RETURN SeqVal;
  END GetLIMSSeqNo;

/*----------------------------------------------------------------------*/
/* This routine is used to create the unique ID for internal work       */
/* order a internal work order defines a complete order comming from    */
/* one customer.                                                        */
  PROCEDURE GetOrderName (
     p_SampleDate IN  SampleList.SampleDate%TYPE,
     p_LocName    IN  SampleList.LocName%TYPE,
     p_MatName    IN  SampleList.MatName%TYPE,
     p_PlantCode  IN  PlantItem.PlantCode%TYPE,
     p_OrderName  OUT LabOrder.OrderName%TYPE
    ) IS

    Plant  VARCHAR2(20);
    Mat    VARCHAR2(20);
    Loc    VARCHAR2(20);
    SeqNb  NUMBER;
  BEGIN /* GetOrderName */
    Plant := SUBSTR(NVL(p_PlantCode, ''), 1, 2); -- first 2 characters
    Mat := SUBSTR(NVL(p_MatName, ''), 1, 2); -- first 2 characters
    Loc := SUBSTR(NVL(p_LocName, ''), 1, 2); -- first 2 characters
    SeqNb := GetLIMSSeqNo (NVL(Plant || Mat || Loc, ' '), p_SampleDate);
    p_OrderName := Plant || '_' || Mat || '_' || Loc || '_' || TO_CHAR(SeqNb);
  END GetOrderName;

/*----------------------------------------------------------------------*/
