/*  Get Sort Name from KM Materials Sub Routine  */ 
PROCEDURE GetSortName(P_RealSigName SignalItem.Signame%TYPE, P_TextSigName SignalItem.Signame%TYPE) IS 
CodeVal MaterialItem.MatCode%TYPE; 
TextVal SignalValue.SigTextValue%TYPE; 
CURSOR C1(P_MatCode MaterialItem.MatCode%TYPE) IS 
 SELECT MatName 
   FROM MaterialItem 
  WHERE MatCode=P_MatCode 
    AND SigId=(select SigId from signalitem where Datatype=4 and signame = P_TextSigName); 
BEGIN 
  CodeVal := NVL(TO_CHAR(ROUND(Get_Value(P_RealSigName))),'-'); 
 
  IF CodeVal<> '-' THEN 
     OPEN C1(CodeVal); 
     FETCH C1 INTO TextVal; 
     IF C1%FOUND THEN 
        Set_TextVal(P_TextSigName,TextVal); 
     ELSE 
        Set_TextVal(P_TextSigName,CodeVal); 
     END IF; 
  CLOSE C1; 
  END IF; 
 
END GetSortName; 
 
