 Set_Value('Boolean_HP',1); 
 Set_Value('Boolean_HC',0); 
Else 
 Set_Value('Boolean_HP',0); 
 Set_Value('Boolean_HC',1); 
End If; 
End TimeBasedBoolean; 
 
 
