/*----------------------------------------------------------------------*/
/* Merge duplicate numerial values                                      */
/*----------------------------------------------------------------------*/
PROCEDURE MergeNumValues (
   p_ValueList IN  ValueListType,
   p_BandWidth IN  AnalysisMeasure.BandWidth%TYPE,
   p_CalcRule  IN  NUMBER,
   p_CalcValue OUT MeasureHistory.NumValue%TYPE,
   p_Status    OUT MeasureHistory.Status%TYPE
  ) IS

  l_ValueList   ValueListType;
  i             BINARY_INTEGER;
  Total         NUMBER := 0;
  n             NUMBER := 0;
  Average       NUMBER;
  Bad           NUMBER := 0;
  MaxBad        NUMBER;
  Diff          NUMBER;
  MaxDiff       NUMBER;
  MaxDiffIndex  NUMBER;
  MaxValue      NUMBER;
BEGIN /* MergeNumValues */
  l_ValueList := p_ValueList;

  /* Validate input parameters */
  IF (p_BandWidth = -1) THEN
    IF CIMSLIB.Debug THEN
      CIMSLIB.LogError ('MergeNumValues: BandWidth = -1, do not merge');
    END IF;
    RETURN;
  END IF;

  p_Status := SmplLib.STATUS_OK;

  /* Calculate average */
  i := l_ValueList.FIRST;
  WHILE i IS NOT NULL LOOP
    IF (l_ValueList(i).NumValue IS NOT NULL) THEN
      Total := Total + l_ValueList(i).NumValue;
      n := n + 1;
    END IF;
    i := l_ValueList.NEXT(i);
  END LOOP;
  IF (n > 0) THEN
    Average := Total / n;
  ELSE
    IF CIMSLIB.Debug THEN
      CIMSLIB.LogError ('MergeNumValues: Empty value list');
    END IF;
    RETURN;
  END IF;

  /* Setup exit criteria */
  IF (p_CalcRule = CALC_RULE_1) THEN
    MaxBad := n / 2;
  END IF;

  IF CIMSLIB.Debug THEN
    CIMSLIB.LogError ('MergeNumValues: ' ||
                      ' MaxBad=' || TO_CHAR(MaxBad) ||
                      ' Average=' || TO_CHAR(Average));
  END IF;

  LOOP
    MaxDiff := 0;
    MaxDiffIndex := NULL;

    /* Search for outliers */
    i := l_ValueList.FIRST;
    WHILE i IS NOT NULL LOOP
      Diff := ABS(l_ValueList(i).NumValue - Average);

      IF (Diff > p_BandWidth/100 * Average) THEN
        IF (Diff > MaxDiff) THEN
          MaxDiff := Diff;
          MaxDiffIndex := i;
          MaxValue := l_ValueList(i).NumValue;
        END IF;
      END IF;

      i := l_ValueList.NEXT(i);
    END LOOP;

    /* Remove outlier */
    IF (MaxDiffIndex IS NOT NULL) THEN
      l_ValueList.DELETE(MaxDiffIndex);
      Bad := Bad + 1;
      IF (Bad >= MaxBad) THEN
        p_Status := SmplLib.STATUS_ERR4;
        p_CalcValue := NULL;
        EXIT;
      END IF;
      Total := Total - MaxValue;
      Average := Total / (n - Bad);
      IF CIMSLIB.Debug THEN
        CIMSLIB.LogError ('MergeNumValues: Exclude this value' ||
                          ' Bad=' || TO_CHAR(Bad) ||
                          ' NumValue=' || TO_CHAR(MaxValue) ||
                          ' NewAverage=' || TO_CHAR(Average));
      END IF;

    ELSE
      p_Status := SmplLib.STATUS_OK;
      p_CalcValue := Average;
      EXIT;
    END IF;
  END LOOP;

  IF CIMSLIB.Debug THEN
    CIMSLIB.LogError ('MergeNumValues: ' ||
                      ' CalcRule=' || TO_CHAR(p_CalcRule) ||
                      ' CalcValue=' || TO_CHAR(p_CalcValue) ||
                      ' Status=' || TO_CHAR(p_Status));
  END IF;
END MergeNumValues;

/*----------------------------------------------------------------------*/
/* Merge duplicate text values                                          */
/*----------------------------------------------------------------------*/
PROCEDURE MergeTextValues (
   p_ValueList IN  ValueListType,
   p_BandWidth IN  AnalysisMeasure.BandWidth%TYPE,
   p_CalcRule  IN  NUMBER,
   p_CalcValue OUT MeasureHistory.TextValue%TYPE,
   p_Status    OUT MeasureHistory.Status%TYPE
  ) IS

  l_ValueList  ValueListType;
  i            BINARY_INTEGER;
  j            BINARY_INTEGER;
  MaxBad       NUMBER;
  CurrText     MeasureHistory.TextValue%TYPE;
  NrText       NUMBER;
  MaxText      MeasureHistory.TextValue%TYPE;
  MaxNrText    NUMBER;
BEGIN /* MergeTextValues */
  l_ValueList := p_ValueList;

  /* Validate input parameters */
  IF (p_BandWidth = -1) THEN
    IF CIMSLIB.Debug THEN
      CIMSLIB.LogError ('MergeTextValues: BandWidth = -1, do not merge');
    END IF;
    RETURN;
  END IF;

  /* Setup exit criteria */
  IF (p_CalcRule = CALC_RULE_1) THEN
    MaxBad := l_ValueList.COUNT / 2;
  END IF;

  IF CIMSLIB.Debug THEN
    CIMSLIB.LogError ('MergeTextValues: ' ||
                      ' MaxBad=' || TO_CHAR(MaxBad));
  END IF;

  MaxNrText := 0;

  i := l_ValueList.FIRST;
  WHILE i IS NOT NULL LOOP

    CurrText := l_ValueList(i).TextValue;
    NrText := 0;

    /* Search for most common text */
    j := i;
    WHILE j IS NOT NULL LOOP
      IF (l_ValueList(j).TextValue = CurrText) THEN
        NrText := NrText + 1;
      END IF;
      j := l_ValueList.NEXT(j);
    END LOOP;

    IF (NrText > MaxNrText) THEN
      MaxNrText := NrText;
      MaxText := CurrText;
    END IF;

    i := l_ValueList.NEXT(i);
  END LOOP;

  IF (MaxNrText > (l_ValueList.COUNT - MaxBad)) THEN
    p_Status := SmplLib.STATUS_OK;
    p_CalcValue := MaxText;
  ELSE
    p_Status := SmplLib.STATUS_ERR4;
    p_CalcValue := NULL;
  END IF;

  IF CIMSLIB.Debug THEN
    CIMSLIB.LogError ('MergeTextValues: ' ||
                      ' CalcRule=' || TO_CHAR(p_CalcRule) ||
                      ' CalcValue=' || p_CalcValue ||
                      ' Status=' || TO_CHAR(p_Status));
  END IF;
END MergeTextValues;

