  CREATE OR REPLACE EDITIONABLE PACKAGE BODY "BMI_CIMS"."SESIMPORT" AS

  PROCEDURE SESSampleFIFO (
				p_SigName		IN	SignalItem.SigName%TYPE,
				p_SigNumValue   	IN	SampleFIFO.SigNumValue%TYPE,
				p_MatName	   	IN	SampleFIFO.SigTextValue%TYPE,
                                p_RiClass		IN	RepItem.RiClass%TYPE,
				SampleDate		IN	VARCHAR,
				p_ObjUnit		IN	SignalItem.ObjUnit%TYPE,
				ChannelNumber		IN  	NUMBER,
                                p_ObjDescr              IN      SignalItem.ObjDescr%TYPE,
				CreateLog		IN	NUMBER  ) IS


	i			BINARY_INTEGER := 0;
	p_SigId			SignalItem.SigId%TYPE;
	p_Ricode		RepItem.Ricode%TYPE;
	p_SampleSeq		SampleFifo.SampleSeq%TYPE;
    	p_Deletion		SampleFIFO.Deletion%TYPE := 'N';

  BEGIN  /*insertSampleFIFO*/

    BEGIN
	/* read SigId from SignalItem */
      SELECT SignalItem.SigId INTO p_SigId
        FROM SignalItem
        WHERE SigName = LTRIM( RTRIM( p_SigName )) and ChanNr = ChannelNumber ;



      EXCEPTION
	    WHEN NO_DATA_FOUND  THEN
	      /* create Signal in SignalItemtable */
		  /* get sequence number for signal */
		  SELECT SignalItemSeq.NEXTVAL INTO p_SigId FROM DUAL;
		  INSERT INTO SignalItem
			( SigId, SigName, AliasName, ChanNr, SigType, DataType,
			  ObjDescr, ObjUnit, DefDate )
		  VALUES( p_SigId, p_SigName, SUBSTR(p_SigName,1,40), ChannelNumber, 0, 1,
			  SUBSTR(p_ObjDescr,1,80), p_ObjUnit, SYSDATE);
		  IF (CreateLog = 1 ) THEN
		    SELECT RepItemSeq.NEXTVAL INTO p_Ricode FROM DUAL;
		    INSERT INTO RepItem
		      (Ricode, RiClass, LogClass, Ritext, AliasName, RiUnit,
		       VSigId1, Description, SortRicode, RiDecimals, DefDate)
			VALUES (p_Ricode, p_RiClass, 'L', p_SigName, SUBSTR(p_SigName,1,40), p_ObjUnit,
			        p_SigId, SUBSTR(p_ObjDescr,1,80), -1, 2, SYSDATE);
		  END IF;
		  COMMIT WORK;

        WHEN OTHERS THEN
          CIMSLIB.LogError ('SESIMPORT.SESSampleFIFO: Unhandled Error on read SigId from SignalItem', 'E', SQLCODE);
    END;

	/* insert value in SampleFIFO table */
	SELECT SampleFifoSeq.Nextval INTO p_SampleSeq FROM DUAL;
    INSERT INTO SampleFIFO
              ( SampleSeq,
          	SigId,
          	Datum,
            	Deletion,
            	SigNumValue,
		SigTextValue )
    VALUES( p_SampleSeq,
        	p_SigId,
		TO_DATE( SampleDate, 'DD-MM-YYYY HH24:Mi:SS'),
                p_Deletion,
                p_SigNumValue,
		p_MatName);
	COMMIT WORK;
    /* CIMSLIB.LogError ('SESIMPORT.SESSampleFIFO: Insert ' || SampleDate || ' Sigid:' || p_sigid || '=' || p_signumvalue, 'I');*/


	/* update signalvalue */
	UPDATE SignalValue
	SET SignalValue.UpdateTime = TO_DATE( SampleDate,'DD-MM-YYYY HH24:Mi:SS'),
	    SignalValue.SigNumValue = p_SigNumValue,
            SignalValue.SigTextValue = p_MatName
	WHERE SignalValue.SigId = p_SigId;

	IF SQL%NOTFOUND THEN
	  /* insert value into SignalValue */
	  INSERT INTO SignalValue ( SigId, UpdateTime, SigNumValue, SigTextValue  )
	  VALUES( p_SigID,
		  TO_DATE( SampleDate, 'DD-MM-YYYY HH24:Mi:SS'),
		  p_SigNumValue,
		  p_MatName );
	END IF;

    COMMIT WORK;

    EXCEPTION
      WHEN OTHERS THEN
        CIMSLIB.LogError ('SESIMPORT.SESSampleFIFO: Unhandled error.', 'E', SQLCODE);

  END SESSampleFIFO ;

  PROCEDURE GetMaterial (	p_MatCode	   	IN	MaterialItem.MatCode%TYPE,
                                p_MatName		OUT	VARCHAR2  ) IS
  BEGIN  /*GetMaterial*/
    BEGIN
	/* convert matcode into matname */
      SELECT MaterialItem.MatName INTO p_MatName
        FROM MaterialItem
       WHERE MatCode = p_MatCode and rownum<2;

      If substr(p_MatCode, -6) = '000000' Then
         CIMSLIB.LogError ('SESIMPORT.GetMaterial: No Material for Matcode ' || p_MatCode || ' found.', 'W', SQLCODE, 'Pli_Vicat_SES');
      End If;

      EXCEPTION
	WHEN NO_DATA_FOUND  THEN
          CIMSLIB.LogError ('SESIMPORT.GetMaterial: No Material for Matcode ' || p_MatCode || ' found.', 'W', SQLCODE, 'Pli_Vicat_SES');

        WHEN OTHERS THEN
          CIMSLIB.LogError ('SESIMPORT.GetMaterial: Unhandled Error', 'E', SQLCODE);
    END;
  END GetMaterial;

  PROCEDURE GetLIMSMaterial (	p_MatCode	   	IN	LIMSMaterial.MatName%TYPE,
                                p_MatName		OUT	VARCHAR2  ) IS
  BEGIN  /*GetLIMSMaterial*/
    BEGIN
	/* convert matcode into matname */
      SELECT LIMSMaterial.MatName INTO p_MatName
        FROM LIMSMaterial
       WHERE AliasName = p_MatCode and rownum<2;

      If substr(p_MatCode, -6) = '000000' Then
         CIMSLIB.LogError ('SESIMPORT.GetLIMSMaterial: No Material for Alias ' || p_MatCode || ' found.', 'W', SQLCODE, 'Pli_Vicat_SES');
      End If;

      EXCEPTION
	WHEN NO_DATA_FOUND  THEN
          CIMSLIB.LogError ('SESIMPORT.GetLIMSMaterial: No Material for Alias ' || p_MatCode || ' found.', 'W', SQLCODE, 'Pli_Vicat_SES');

        WHEN OTHERS THEN
          CIMSLIB.LogError ('SESIMPORT.GetLIMSMaterial: Unhandled Error', 'E', SQLCODE);
    END;
  END GetLIMSMaterial;

  PROCEDURE GetLocation (	p_LocAlias	   	IN	SampleLocation.AliasName%TYPE,
                                p_LocName		OUT	SampleLocation.LocName%TYPE ) IS
  BEGIN  /*GetLocation*/
    BEGIN
	/* convert SampleLocationAlias into SampleLocationName */
        /* Pattern * and ? can be used too                     */
      SELECT SampleLocation.LocName INTO p_LocName
        FROM SampleLocation
       WHERE p_LocAlias like Replace(Replace(AliasName,'*','%'),'?','_') and rownum<2;

      EXCEPTION
	WHEN NO_DATA_FOUND  THEN
          CIMSLIB.LogError ('SESIMPORT.GetLocation: No SampleLocation for LocationAlias ' || p_LocAlias || ' found.', 'W', SQLCODE, 'Pli_Vicat_SES');

        WHEN OTHERS THEN
          CIMSLIB.LogError ('SESIMPORT.GetLocation: Unhandled Error', 'E', SQLCODE);
    END;
  END GetLocation;

  PROCEDURE FindFilename (	p_Filename	   	IN	DispatchFilebuffer.Filename%TYPE,
                                p_found  		OUT	NUMBER ) IS

  BEGIN  /*FindFilename*/
    BEGIN
	/* checks for existing filename in table DispatchFileBuffer */
        /* returns >0 if found        */
      SELECT count(*) INTO p_found
        FROM DispatchFilebuffer
       WHERE upper(Filename) = upper(p_Filename);

      EXCEPTION
        WHEN OTHERS THEN
          CIMSLIB.LogError ('SESIMPORT.FindFilename: Unhandled Error', 'E', SQLCODE);
    END;
  END FindFilename;


  PROCEDURE InsertFilename (	p_Filename	   	IN	DispatchFilebuffer.Filename%TYPE) IS

  p_found NUMBER :=0;
  BEGIN  /*InsertFilename*/
    BEGIN
        /* check for existing filename first */
      SELECT count(*) INTO p_found
        FROM DispatchFilebuffer
       WHERE upper(Filename) = upper(p_Filename);

       IF (p_found = 0) THEN
	  /* inserts records in table Dispatchbuffer */
          INSERT INTO DispatchFilebuffer
          VALUES (p_Filename, Sysdate);
       END IF;

      EXCEPTION
        WHEN OTHERS THEN
          CIMSLIB.LogError ('SESIMPORT.InsertFilename: Unhandled Error', 'E', SQLCODE);
    END;
  END InsertFilename;

  PROCEDURE DeleteFilename (	p_Period                IN      NUMBER) IS

  BEGIN  /*DeleteFilename*/
    BEGIN
	/* deletes older records in table Dispatchbuffer */
      DELETE
        FROM DispatchFilebuffer
       WHERE Insert_Date < (Sysdate - p_Period);

      EXCEPTION
        WHEN OTHERS THEN
          CIMSLIB.LogError ('SESIMPORT.DeleteFilename: Unhandled Error', 'E', SQLCODE);
    END;
  END DeleteFilename;

  PROCEDURE FindDispatchdata (	p_DType	   	IN	DispatchDatabuffer.DType%TYPE,
                                p_SampleDate  	IN	VARCHAR,
                                p_SAPCode  	IN	DispatchDatabuffer.SAPCode%TYPE,
   				p_found		OUT	NUMBER ) IS

  BEGIN  /*FindDispatchdata*/
    BEGIN
	/* checks for existing record in table DispatchDatabuffer */
        /* returns >0 if found           */
      SELECT count(*) INTO p_found
        FROM DispatchDatabuffer
       WHERE upper(DType) = upper(p_DType)
         AND SampleDate = p_SampleDate
         AND SAPCode = p_SAPcode;

      EXCEPTION
        WHEN OTHERS THEN
          CIMSLIB.LogError ('SESIMPORT.FindDispatchdata: Unhandled Error', 'E', SQLCODE);
    END;
  END FindDispatchdata;

  PROCEDURE InsertDispatchData (p_DType	   	IN	DispatchDatabuffer.DType%TYPE,
                                p_SampleDate  	IN	VARCHAR,
                                p_SAPCode  	IN	DispatchDatabuffer.SAPCode%TYPE) IS

  p_found NUMBER :=0;
  BEGIN  /*InsertDispatchData*/
    BEGIN
        /* check for existing records first */
      SELECT count(*) INTO p_found
        FROM DispatchDatabuffer
       WHERE upper(DType) = upper(p_DType)
         AND SampleDate = p_SampleDate
         AND SAPCode = p_SAPcode;

       IF (p_found = 0) THEN
	  /* inserts records in table DispatchDatabuffer */
          INSERT INTO DispatchDatabuffer
          VALUES (p_DType, p_SampleDate, p_SAPCode);
       END IF;

      EXCEPTION
        WHEN OTHERS THEN
          CIMSLIB.LogError ('SESIMPORT.InsertDispatchData: Unhandled Error', 'E', SQLCODE);
    END;
  END InsertDispatchData;


  PROCEDURE DeleteDispatchData (	p_Period                IN      NUMBER) IS

  BEGIN  /*DeleteDispatchData*/
    BEGIN
	/* deletes older records in table DispatchDatabuffer */
      DELETE
        FROM DispatchDatabuffer
       WHERE SampleDate < (Sysdate - p_Period);

      EXCEPTION
        WHEN OTHERS THEN
