/*****************************************************************************/
/* Dummy body                                                                */
/* The shift plan is plant specific                                          */
/* This dummy implementation assumes a 4 shift plan. The 4 shifts A,B,C and D*/
/* rotates in a sequential order starting from 1.1.96 06:00                  */
/*****************************************************************************/

/*****************************************************************************/
/* CONSTANTS, VARIABLES                                                      */
/*****************************************************************************/

/*****************************************************************************/
/* PRIVATE PROCEDURES                                                        */
/*****************************************************************************/

/*****************************************************************************/
/* PUBLIC PROCEDURES                                                         */
/*****************************************************************************/

  PROCEDURE UpdateShiftGroupLog (
    P_PinstStart IN DATE,                       -- Date of period to be treated
    P_PinstID    IN PeriodInstance.PinstID%TYPE -- ID of the period instance
  ) IS
  /**************************************************************/
  /* Updates the ShiftGroupLog for PinstID                      */
  /**************************************************************/

    StdShtGrp  CONSTANT VARCHAR2(10) := 'SHT_Test';
    GrpID      ShiftGroupLog.GroupID%TYPE;
  BEGIN
    GetShiftGroup (P_PinstStart, StdShtGrp, GrpID);

    UPDATE ShiftGroupLog
       SET GroupID = GrpID
     WHERE PinstID = P_PinstID;
    IF SQL%NOTFOUND THEN
       /* Insert Record */
       INSERT
         INTO ShiftGroupLog (PInstId, Section, GroupID)
       VALUES (P_PInstID, StdShtGrp, GrpID);
    END IF;

    EXCEPTION
      WHEN OTHERS THEN
        CIMSLIB.LogError ('UpdateShiftGroupLog: Unhandled error.',
                          'E', SQLCODE);
  END UpdateShiftGroupLog;


  PROCEDURE GetShiftGroup (
    ShiftStart   IN  DATE,
    Section      IN  ShiftGroupLog.Section%TYPE,
    GroupID      OUT ShiftGroupLog.GroupID%TYPE,
    p_SplitStop  IN  BOOLEAN := FALSE,
    p_OldGroupId IN  ShiftGroupLog.GroupID%TYPE := NULL
  ) IS
  /********************************************************************/
  /* Retruns the GroupID for a shift                                  */
  /********************************************************************/

    ShiftRef  CONSTANT DATE := TO_DATE ('1.1.1996 06:00','DD.MM.YYYY HH24:MI');
  BEGIN
     /* Shift 1 06:00 - 14:00                 */
     /* Shift 2 14:00 - 22:00                 */
     /* Shift 3 22:00 - 06:00                 */
     /* Shift 4 06:00 - 14:00                 */
    GroupID := SUBSTR (Section,1,7) || '_' ||
               TO_CHAR (MOD(TRUNC((ShiftStart-ShiftRef)*3),3)+1);
  END GetShiftGroup;

END ShiftLog;
