  CREATE OR REPLACE EDITIONABLE FUNCTION "BMI_CIMS"."CHECKFORCOMMENT" (
    p_PInstStart IN CIMSComment.PInstStart%TYPE,
    p_RemoteId   IN CIMSComment.RemoteId%TYPE,
    p_PType      IN CIMSComment.PType%TYPE := NULL,
    p_SortName   IN CIMSComment.SortName%TYPE := NULL,
    p_TypeId     IN CIMSComment.TypeId%TYPE := 4) RETURN NUMBER IS

  CURSOR c1 IS
    SELECT 1 FROM CIMSComment
     WHERE TypeId = p_TypeId
       AND PInstStart = p_PInstStart
       AND PType = p_PType
       AND (SortName = p_SortName OR p_SortName IS NULL)
       AND RemoteId = p_RemoteId;

  x NUMBER;

BEGIN
  OPEN c1;
  FETCH c1 INTO x;
  RETURN x;
END CheckForComment;
