--  DDL for Function DECODEFLOAT
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE FUNCTION "BMI_CIMS"."DECODEFLOAT" (v    IN CHAR,
                                        ofst IN CHAR) RETURN NUMBER IS
/****************************************************************/
/* convert an IEEE float to an Oracle number                    */
/* v is a 8 character hex string of an single pressision number */
/* in IEEE format                                               */
/****************************************************************/
  i number;
  val number := 0;
  exp number := 0;
  c char;
BEGIN
  IF SUBSTR (ofst,1,1) >= '8' THEN
    /* Negative offset indicates NULL value */
    RETURN NULL;
  END IF;
  IF v = '00000000' THEN
    RETURN 0;
  END IF;
  -- decode mantisa (first 6 characters)
  c := SUBSTR(v,5,1);
  IF c < '8' THEN
    -- add implict 1. to mantissa
    -- not required in other cases because MSB bit is 1
    val := ascii(c) - ascii('0')+8;
  ELSIF c <= '9' THEN
    val := ascii(c) - ascii('0');
  ELSE
     val := ascii(c) - ascii('A')+10;
  END IF;
  c := SUBSTR(v,6,1);
  IF c <= '9' THEN
    val := (val * 16)+ascii(c) - ascii('0');
  ELSE
    val := (val * 16)+ascii(c) - ascii('A')+10;
  END IF;
  FOR i IN REVERSE 0 .. 1 LOOP
    c := SUBSTR(v,i*2+1,1);
    IF c <= '9' THEN
      val := (val * 16)+ascii(c) - ascii('0');
    ELSE
      val := (val * 16)+ascii(c) - ascii('A')+10;
    END IF;
    c := SUBSTR(v,i*2+2,1);
    IF c <= '9' THEN
      val := (val * 16)+ascii(c) - ascii('0');
    ELSE
      val := (val * 16)+ascii(c) - ascii('A')+10;
    END IF;
  END LOOP;
  -- handle exponent
  c := SUBSTR(v,7,1);
  IF c >= '8' OR c < '0' THEN
    -- sign bit is set
    val := -val;
    exp := exp-8;
  END IF;
  IF c <= '9' THEN
    exp := exp+ascii(c) - ascii('0');
  ELSE
    exp := exp+ascii(c) - ascii('A')+10;
  END IF;
  c := SUBSTR(v,8,1);
  IF c <= '9' THEN
    exp := (exp * 16)+ascii(c) - ascii('0');
  ELSE
    exp := (exp * 16)+ascii(c) - ascii('A')+10;
  END IF;
  exp := exp+exp;
  c := SUBSTR(v,5,1);
  if c >= '8' THEN
    exp := exp +1;
  END IF;
  exp := exp - 127-23;
  RETURN val * power(2,exp);
END decodeFloat;
