  CREATE OR REPLACE EDITIONABLE FUNCTION "BMI_CIMS"."DECODEOFFSET" (PinstStart IN DATE,
                                            Offsets IN VARCHAR,
                                                  i IN NUMBER,
                                        offsetScale IN NUMBER)
                                      RETURN DATE IS
/****************************************************************/
/* convert binary offset to an Oracle date                      */
/****************************************************************/
  val number := 0;
  offst NUMBER;
  c char;
  c1 char;
BEGIN
  IF Offsets IS NULL THEN
    val := i+1;
  ELSE
    offst := (i*2)+1;
    c1 := SUBSTR(Offsets,offst,1);
    IF c1 <= '9' THEN
      val := ascii(c1) - ascii('0');
    ELSE
      val := ascii(c1) - ascii('A')+10;
    END IF;
    c := SUBSTR(Offsets,offst+1,1);
    IF c <= '9' THEN
      val := (val * 16)+ascii(c) - ascii('0');
    ELSE
      val := (val * 16)+ascii(c) - ascii('A')+10;
    END IF;
    IF c1 >= '8' OR c1 < '0' THEN
      /* calc byte One's complement of val */
      val := 256-val ;
    END IF;
  END IF;
  RETURN PinstStart + (val * OffsetScale / (24 * 3600));
END decodeOffset;
